import React, { useState, useEffect, useRef } from "react";
import { Search, Filter } from "lucide-react";
import "../styles/KnowledgeBase.css";

const KnowledgeBase = () => {
  const [query, setQuery] = useState("");
  const [results, setResults] = useState([]);
  const [filteredResults, setFilteredResults] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const resultsPerPage = 5;
  const [showingSearchResults, setShowingSearchResults] = useState(false);
  const [selectedFilterCategory, setSelectedFilterCategory] = useState("");
  const [selectedFilterValue, setSelectedFilterValue] = useState("");
  const [showFilter, setShowFilter] = useState(false);
  const cache = useRef( new Map());
  const [originalData,setOriginalData] =useState([]);

  const filterOptions = {
    "Impact Level": ["Low", "Medium", "High", "Critical"],
    "Issue Type": ["Malware", "Phishing", "DDoS", "Insider Threat", "Social Engineering"],
    "Source IP": ["192.168.1.1", "10.0.0.2"],
    "Affected IP": ["172.16.5.4", "192.168.10.10"],
    "Date/Time Logged": ["2024-01-01", "2024-02-15"]
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch("http://192.168.1.47:5000/api/search?query=");
        const data = await response.json();
        setOriginalData(data);
        setResults(data);
        setFilteredResults(data);
      } catch (error) {
        console.error("Error fetching initial data:", error);
      }
    };
    fetchData();
  }, []);


  useEffect(() => {
    const fetchAndFilter = async () => {
      let baseData = results;
  
      if (query.trim() !== "") {
        await searchWithCache(query);
        baseData = cache.current.get(query.toLowerCase()) || [];
      }
  
      let filtered = baseData;
  
      if (selectedFilterCategory && selectedFilterValue) {
        const filterKey = selectedFilterCategory
          .toLowerCase()
          .replace(/\s|\//g, "")
          .replace("impactlevel", "impact")
          .replace("issuetype", "type")
          .replace("sourceip", "source_ip")
          .replace("affectedip", "affected_ip")
          .replace("datetimelogged", "datetime");
  
        filtered = filtered.filter((item) => {
          try {
            const propsObj = JSON.parse(item.props);
            const value = propsObj[filterKey];
            return (
              value &&
              value.toString().toLowerCase() === selectedFilterValue.toLowerCase()
            );
          } catch (err) {
            return false;
          }
        });
      }
  
      setFilteredResults(filtered);
      setCurrentPage(1);
      setShowingSearchResults(
        query.trim() !== "" || (selectedFilterCategory && selectedFilterValue)
      );
    };
  
    fetchAndFilter();
  }, [query, selectedFilterCategory, selectedFilterValue,results]);
  
  
  const searchWithCache = async (queryText) => {
    const lowerQuery = queryText.toLowerCase();
  
    if (cache.current.has(lowerQuery)) {
      console.log("Using cached data...");
      const cachedData = cache.current.get(lowerQuery);
      setResults(cachedData);
      setFilteredResults(cachedData);
    } else {
      console.log("Fetching from API...");
      try {
        const response = await fetch(`http://192.168.1.47:5000/api/search?query=${lowerQuery}`);
        const data = await response.json();
        cache.current.set(lowerQuery, data); // Save to cache
        setResults(data);
        setFilteredResults(data);
      } catch (error) {
        console.error("API fetch failed:", error);
      }
    }
  };
  



  const handleGoBack = () => {
    setQuery("");
    setSelectedFilterCategory("");
    setSelectedFilterValue("");
    setResults(originalData);
    setFilteredResults(originalData);
    setCurrentPage(1);
    setShowingSearchResults(false);
  };

  const paginatedResults = filteredResults.slice(
    (currentPage - 1) * resultsPerPage,
    currentPage * resultsPerPage
  );

  const totalPages = Math.ceil(filteredResults.length / resultsPerPage);

  return (
    <div className="kb-container">
      <div className="kb-card">
    

        <div className="kb-search-bar">
          <input
            type="text"
            placeholder="Search e.g. Ransomware, Phishing, 1.1.1.1"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            className="kb-input"
          />
          <button className="kb-button">
            <Search size={16} /> Search
          </button>
          <button
            onClick={() => setShowFilter(!showFilter)}
            className="kb-button"
          >
            <Filter size={16} /> Filter
          </button>
        </div>

        {showFilter && (
          <div className="kb-filter-options">
            <div className="kb-filter-dropdown">
              <label>Select Filter Category:</label>
              <select
                value={selectedFilterCategory}
                onChange={(e) => {
                  setSelectedFilterCategory(e.target.value);
                  setSelectedFilterValue("");
                }}
              >
                <option value="">-- Select Category --</option>
                {Object.keys(filterOptions).map((category) => (
                  <option key={category} value={category}>
                    {category}
                  </option>
                ))}
              </select>
            </div>

            {selectedFilterCategory && (
              <div className="kb-filter-dropdown">
                <label>Select Value:</label>
                <select
                  value={selectedFilterValue}
                  onChange={(e) => setSelectedFilterValue(e.target.value)}
                >
                  <option value="">-- Select Value --</option>
                  {filterOptions[selectedFilterCategory].map((value) => (
                    <option key={value} value={value}>
                      {value}
                    </option>
                  ))}
                </select>
              </div>
            )}
          </div>
        )}

        <div className="kb-results">
          <table className="kb-table">
            <thead>
              <tr>
                <th>S.No.</th>
                <th>Issue Name</th>
                <th>Description</th>
                <th>type</th>
                <th>Impact</th>
                <th>Solution</th>
                <th>Tag</th>
                <th>Status</th>
                <th>Create By</th>
                <th>Created At</th>
                <th>Resolve_by</th>
                <th>Resolve_At</th>
              </tr>
            </thead>
            <tbody>
              {paginatedResults.map((res, index) => (
                <tr key={res.id}>
                  <td>{(currentPage - 1) * resultsPerPage + index + 1}</td>
                  <td>{res.issue_name}</td>
                  <td>{res.description}</td>
                  <td>{res.type}</td>
                  <td>{res.impact}</td>
                  <td>{res.solution}</td>
                  <td>{res.tags}</td>
                  <td>{res.status}</td>
                  <td>{res.created_by}</td>
                  <td>{res.created_at}</td>
                  <td>{res.resolved_by}</td>
                  <td>{res.resolved_at}</td>
                </tr>
              ))}
              {paginatedResults.length === 0 && (
                <tr>
                  <td colSpan={3} className="kb-no-results">
                    No results found.
                  </td>
                </tr>
              )}
            </tbody>
          </table>

          {filteredResults.length > resultsPerPage && (
            <div className="kb-pagination">
              <button
                onClick={() => setCurrentPage((prev) => Math.max(prev - 1, 1))}
                disabled={currentPage === 1}
              >
                Previous
              </button>
              <span>
                Page {currentPage} of {totalPages}
              </span>
              <button
                onClick={() => setCurrentPage((prev) => Math.min(prev + 1, totalPages))}
                disabled={currentPage === totalPages}
              >
                Next
              </button>
            </div>
          )}

          {showingSearchResults && (
            <div className="kb-back">
              <button onClick={handleGoBack} className="kb-button kb-back-button">
                ← Back to All Data
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default KnowledgeBase;
