import React, { useState, useEffect, useRef } from "react";
import { Search } from "lucide-react";
import "../styles/KnowledgeBase.css";

const KnowledgeBase = () => {
  const [query, setQuery] = useState("");
  const [results, setResults] = useState([]);
  const [filteredResults, setFilteredResults] = useState([]);  // Ensure this is always an array
  const [currentPage, setCurrentPage] = useState(1);
  const resultsPerPage = 5;
  const [showingSearchResults, setShowingSearchResults] = useState(false);
  const cache = useRef(new Map());
  const [originalData, setOriginalData] = useState([]);
  const [file, setFile] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch("http://192.168.1.47:5000/api/search?query=");
        const data = await response.json();
        setOriginalData(data);
        setResults(data);
        setFilteredResults(Array.isArray(data) ? data : []);  // Ensure it is an array
      } catch (error) {
        console.error("Error fetching initial data:", error);
        setFilteredResults([]); // Set empty array if an error occurs
      }
    };
    fetchData();
  }, []);

  useEffect(() => {
    const fetchAndFilter = async () => {
      let baseData = results;

      if (query.trim() !== "") {
        await searchWithCache(query);
        baseData = cache.current.get(query.toLowerCase()) || [];
      }

      setFilteredResults(Array.isArray(baseData) ? baseData : []);  // Ensure it is an array
      setCurrentPage(1);
      setShowingSearchResults(query.trim() !== "");
    };

    fetchAndFilter();
  }, [query, results]);

  const searchWithCache = async (queryText) => {
    const lowerQuery = queryText.toLowerCase();

    if (cache.current.has(lowerQuery)) {
      console.log("Using cached data...");
      const cachedData = cache.current.get(lowerQuery);
      setResults(cachedData);
      setFilteredResults(cachedData);
    } else {
      console.log("Fetching from API...");
      try {
        const response = await fetch(`http://192.168.1.47:5000/api/search?query=${lowerQuery}`);
        const data = await response.json();
        cache.current.set(lowerQuery, data);  // Save to cache
        setResults(data);
        setFilteredResults(Array.isArray(data) ? data : []);  // Ensure it is an array
      } catch (error) {
        console.error("API fetch failed:", error);
        setFilteredResults([]);  // Set empty array on error
      }
    }
  };

  const handleGoBack = () => {
    setQuery("");
    setResults(originalData);
    setFilteredResults(originalData);
    setCurrentPage(1);
    setShowingSearchResults(false);
  };

  const handleUpload = async () => {
    const formData = new FormData();
    formData.append('file', file);
    try {
      const res = await fetch("http://192.168.1.47:5000/upload-excel", {
        method: "POST",
        body: formData
      });
      const data = await res.json();
      alert(data.message);
    } catch (err) {
      alert("Upload failed.");
    }
  };

  // Ensure filteredResults is always an array before calling .slice()
  const paginatedResults = Array.isArray(filteredResults)
    ? filteredResults.slice((currentPage - 1) * resultsPerPage, currentPage * resultsPerPage)
    : [];

  const totalPages = Math.ceil(filteredResults.length / resultsPerPage);

  return (
    <div className="kb-container">
      <div className="kb-card">
        <div className="kb-search-bar">
          <input
            type="text"
            placeholder="Search e.g. Ransomware, Phishing, 1.1.1.1"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            className="kb-input w-1/4"
          />
          <button className="kb-button">
            <Search size={16} /> Search
          </button>

          {/* Import button */}
          <button
            onClick={() => document.getElementById('file-input').click()}
            className="ml-4 bg-blue-500 text-white px-4 py-2 rounded"
          >
            Import Data
          </button>
          <input
            type="file"
            id="file-input"
            accept=".xlsx"
            style={{ display: "none" }}
            onChange={(e) => setFile(e.target.files[0])}
          />
          {file && (
            <button
              onClick={handleUpload}
              className="ml-2 bg-green-500 text-white px-4 py-2 rounded"
            >
              Upload
            </button>
          )}
        </div>

        <div className="kb-results">
          <table className="kb-table">
            <thead>
              <tr>
                <th>S.No.</th>
                <th>Name</th>
                <th>IP Address</th>
                <th>Issue Created</th>
                <th>Issues Description</th>
                <th>Issues Status</th>
                <th>Solution Description</th>
              </tr>
            </thead>
            <tbody>
              {paginatedResults.map((res, index) => (
                <tr key={res.id}>
                  <td>{(currentPage - 1) * resultsPerPage + index + 1}</td>
                  <td>{res.name}</td>
                  <td>{res.ip_address}</td>
                  <td>{res.issues_created}</td>
                  <td>{res.issues_description}</td>
                  <td>{res.client_issue_status}</td>
                  <td>{res.issues_solved_description}</td>
                </tr>
              ))}
              {paginatedResults.length === 0 && (
                <tr>
                  <td colSpan={7} className="kb-no-results">
                    No results found.
                  </td>
                </tr>
              )}
            </tbody>
          </table>

          {filteredResults.length > resultsPerPage && (
            <div className="kb-pagination">
              <button
                onClick={() => setCurrentPage((prev) => Math.max(prev - 1, 1))}
                disabled={currentPage === 1}
              >
                Previous
              </button>
              <span>
                Page {currentPage} of {totalPages}
              </span>
              <button
                onClick={() => setCurrentPage((prev) => Math.min(prev + 1, totalPages))}
                disabled={currentPage === totalPages}
              >
                Next
              </button>
            </div>
          )}

          {showingSearchResults && (
            <div className="kb-back">
              <button onClick={handleGoBack} className="kb-button kb-back-button">
                ‚Üê Back to All Data
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default KnowledgeBase;
