import React, { useState, useEffect, useRef } from "react";
import { Search } from "lucide-react";
import "../styles/KnowledgeBase.css";

const KnowledgeBase = () => {
  const [query, setQuery] = useState("");
  const [results, setResults] = useState([]);
  const [filteredResults, setFilteredResults] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const resultsPerPage = 5;
  const [showingSearchResults, setShowingSearchResults] = useState(false);
  const cache = useRef(new Map());
  const [originalData, setOriginalData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch("http://192.169.1.47/api/search?query=");
        const data = await response.json();
        setOriginalData(data);
        setResults(data);
        setFilteredResults(data);
      } catch (error) {
        console.error("Error fetching initial data:", error);
      }
    };
    fetchData();
  }, []);

  useEffect(() => {
    const fetchAndSearch = async () => {
      if (query.trim() !== "") {
        await searchWithCache(query);
        const data = cache.current.get(query.toLowerCase()) || [];
        setFilteredResults(data);
        setShowingSearchResults(true);
      } else {
        setFilteredResults(originalData);
        setShowingSearchResults(false);
      }
      setCurrentPage(1);
    };

    fetchAndSearch();
  }, [query]);

  const searchWithCache = async (queryText) => {
    const lowerQuery = queryText.toLowerCase();

    if (cache.current.has(lowerQuery)) {
      console.log("Using cached data...");
    } else {
      console.log("Fetching from API...");
      try {
        const response = await fetch("http://192.169.1.47/api/search?query=${lowerQuery}");
        const data = await response.json();
        cache.current.set(lowerQuery, data);
      } catch (error) {
        console.error("API fetch failed:", error);
      }
    }
  };

  const handleImport = async (event) => {
    const file = event.target.files[0];
    if (!file) return;
  
    const formData = new FormData();
    formData.append("file", file);
  
    try {
      const response = await fetch("http://192.169.1.47/api/import", {
        method: "POST",
        body: formData,
      });
  
      const result = await response.json();
      if (response.ok) {
        alert("File imported successfully!");
        window.location.reload(); // Reload to reflect newly added data
      } else {
        alert("Import failed: " + result.message);
      }
    } catch (error) {
      console.error("Error importing file:", error);
      alert("Error uploading file");
    }
  };

  const handleGoBack = () => {
    setQuery("");
    setResults(originalData);
    setFilteredResults(originalData);
    setCurrentPage(1);
    setShowingSearchResults(true);
  };

  const paginatedResults = filteredResults.slice(
    (currentPage - 1) * resultsPerPage,
    currentPage * resultsPerPage
  );

  const totalPages = Math.ceil(filteredResults.length / resultsPerPage);

  return (
    <div className="kb-container">
      <div className="kb-card">
        <div className="kb-search-bar">
          <input
            type="text"
            placeholder="Search e.g. Ransomware, Phishing, 1.1.1.1"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            className="kb-input"
          />
          <button className="kb-button">
            <Search size={16} /> Search
          </button>
          <label htmlFor="import-file" className="kb-button">
            📁 Import
          </label>
          <input
            id="import-file"
            type="file"
            accept=".xlsx, .xls, .csv"
            style={{ display: "none" }}
            onChange={handleImport}
          />
        </div>

        <div className="kb-results">
          <table className="kb-table">
            <thead>
              <tr>
                <th>S.No</th>
                <th>Issue ID</th>
                <th>IP Address</th>
                <th>Title</th>
                <th>Status</th>
                <th>Client Name</th>
                <th>Email</th>
                <th>Location</th>
                <th>Created At</th>
              </tr>
            </thead>
            <tbody>
              {paginatedResults.length > 0 ? (
                paginatedResults.map((item, index) => (
                  <tr key={item.issue_id}>
                    <td>{(currentPage - 1) * resultsPerPage + index + 1}</td>
                    <td>{`ISSUE-${item.issue_id}`}</td> {/* Correct string interpolation */}
                    <td>{item.ip_address}</td>
                    <td>{item.title}</td>
                    <td>{item.status}</td>
                    <td>{item.client_name}</td>
                    <td>{item.client_email}</td>
                    <td>{item.client_location}</td>
                    <td>{item.created_at}</td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan={9} className="kb-no-results">
                    No results found.
                  </td>
                </tr>
              )}
            </tbody>
          </table>

          {filteredResults.length > resultsPerPage && (
            <div className="kb-pagination">
              <button
                onClick={() => setCurrentPage((prev) => Math.max(prev - 1, 1))}
                disabled={currentPage === 1}
              >
                Previous
              </button>
              <span>
                Page {currentPage} of {totalPages}
              </span>
              <button
                onClick={() => setCurrentPage((prev) => Math.min(prev + 1, totalPages))}
                disabled={currentPage === totalPages}
              >
                Next
              </button>
            </div>
          )}

          {showingSearchResults && (
            <div className="kb-back">
              <button onClick={handleGoBack} className="kb-button kb-back-button">
                ← Back to All Data
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default KnowledgeBase;
