import { useState } from "react";
import "../styles/OperationRunbook.css";

// Placeholder components – you’ll define them later
import Assets from "../components/Assets";
import SLA from "../components/SLA";
import EscalationMatrix from "../components/EscalationMatrix";
import Tokens from "../components/Tokens";

const OperationRunbooks = () => {
  const [activeTab, setActiveTab] = useState("Assets");

  const renderTabContent = () => {
    switch (activeTab) {
      case "Assets": return <Assets />;
      case "SLA": return <SLA />;
      case "EscalationMatrix": return <EscalationMatrix />;
      case "Tokens": return <Tokens />;
      default: return null;
    }
  };

  return (
    <div className="runbook-container">
      <h1>Operation Runbook</h1>
      
      <div className="tabs">
        {["Assets", "SLA", "EscalationMatrix", "Tokens"].map((tab) => (
          <button
            key={tab}
            className={`tab ${activeTab === tab ? "active" : ""}`}
            onClick={() => setActiveTab(tab)}
          >
            {tab}
          </button>
        ))}
      </div>

      <div className="tab-content">
        {renderTabContent()}
      </div>
    </div>
  );
};

export default OperationRunbooks;
